import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SideNav from './SideNav';
import { useSetRecoilState } from 'recoil';
import { TokenAtom } from '../Recoil/TokenAtom';
import { useNavigate, useLocation } from 'react-router-dom';

function Login() {
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const setAccessToken = useSetRecoilState(TokenAtom);
    const navigate = useNavigate();

    const location = useLocation();
    const from = location?.state?.redirectedFrom?.pathname || '/'


    const handleSubmit = (event) => {
        event.preventDefault();

        try{
            const BASE_URL = process.env.REACT_APP_SERVER_BASE_URL;
            axios.post(`${BASE_URL}/login`, {customId:id, password}).then((res)=>{
                if(res.status === 200){
                    setAccessToken(res.status);
                    navigate(from);
                }
            })

        } catch(error){
            console.log(error);
        }
    }

    return (
        <div className='Login'>
            {/* SideNavBar 요소를 숨기기 위해 style 속성에 display: none 설정 */}
            <SideNav style={{ display: 'none' }} />
            {/* rightMarginBox 요소를 숨기기 위해 style 속성에 display: none 설정 */}
            <div className="rightMarginBox" style={{ display: 'none' }}>

                    </div>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor='ID'>ID:</label>
                    <input type='text' autoFocus id='id' value={id} onChange={(e) => {setId(e.target.value)}} />
                </div>
                <div>
                    <label htmlFor='password'>Password:</label>
                    <input type='password' id='password' value={password} onChange={(e) => {setPassword(e.target.value)}} />
                </div>
                <button type='submit'>Login</button>
            </form>
        </div>
    );
}

export default Login;
