import React, { useEffect } from 'react';

function BrowserHeightUpdater({ setWebBrowserHeight }) {
  useEffect(() => {
    const updateHeight = () => {
      const height = window.innerHeight;
      setWebBrowserHeight(height);
    };

    window.addEventListener('resize', updateHeight);
    updateHeight();

    return () => {
      window.removeEventListener('resize', updateHeight);
    };
  }, [setWebBrowserHeight]);

  return null; // BrowserHeightUpdater는 실제로 DOM에 나타나지 않으므로 null을 반환합니다.
}

export default BrowserHeightUpdater;
