import { atom, selector } from 'recoil';

// 웹 브라우저 높이를 저장할 atom 생성
export const webBrowserHeightState = atom({
  key: 'webBrowserHeightState',
  default: 0,
});

// 웹 브라우저 높이를 업데이트하는 selector 생성
export const updateWebBrowserHeight = selector({
  key: 'updateWebBrowserHeight',
  get: ({ get }) => {
    const updateHeight = () => {
      const height = window.innerHeight;
      get(webBrowserHeightState); // Selector를 호출하여 값을 가져오게 함
    };

    // 컴포넌트가 마운트될 때와 창의 크기가 변경될 때마다 높이 업데이트
    window.addEventListener('resize', updateHeight);
    updateHeight(); // 컴포넌트가 마운트된 후에도 한 번 호출

    // 컴포넌트가 언마운트될 때 이벤트 리스너 해제
    return () => {
      window.removeEventListener('resize', updateHeight);
    };
  },
});
