/* eslint-disable */
import React, {useState} from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import BrowserHeightUpdater from "./components/BrowserHeightUpdater";
import Login from './pages/Login';
import SideNav from './pages/SideNav';
import Home from './pages/Home';
import Explore from './pages/Explore';

function App() {
  const [webBrowserHeight, setWebBrowserHeight] = useState(0);

  useEffect(() => {
    // 화면 높이만큼 body div에 height를 주는 작업
    const updateHeight = () => {
      const height = window.innerHeight;
      setWebBrowserHeight(height);
    };

    // 컴포넌트가 마운트될 때와 창의 크기가 변경될 때마다 높이 업데이트
    window.addEventListener('resize', updateHeight);
    updateHeight(); // 컴포넌트가 마운트된 후에도 한 번 호출

    // 컴포넌트가 언마운트될 때 이벤트 리스너 해제
    return () => {
      window.removeEventListener('resize', updateHeight);
    };
  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 한 번만 실행되도록 함

  // 쿠키에서 JSESSIONID가 있는지 여부 확인
  // const isLoggedIn = document.cookie.includes('JSESSIONID');
  const isLoggedIn = false;

  return (
    <Router>
      <div className="App">
          <SideNav data = {updateHeight} />
          <div className='homeBox'>
            <Routes>
              {/* 홈 페이지 */}
              <Route path="/" element={<Home />} />

              {/* 탐색 페이지 */}
              <Route path="/explore" element={<Explore />} />

              {/* 로그인 페이지 */}
              <Route path="/login" element={<Login />} />

              {/* 로그인 상태 확인 후 리다이렉트 */}
              <Route
                path="/" element={isLoggedIn ? <Navigate to="/" /> : <Navigate to="/login" />}
              />
            </Routes>
          </div>
      </div>
    </Router>
  );
}

export default App;
