import React, { useState, useRef } from 'react';
import axios from 'axios';
import "./Home.css";

function Home({webBrowserHeight}) {

    const [content, setContent] = useState('');
    const [image, setImage] = useState(null);

    const textarea = useRef();
    const postBoxRef = useRef();

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    const handleChange = (event) => {
        setContent(event.target.value);

        if (textarea.current) {
            textarea.current.style.height = 'auto'; // 높이 초기화
            textarea.current.style.height = textarea.current.scrollHeight + 'px'; // 스크롤 높이만큼 설정
            // div#PostBox의 높이도 업데이트
            if (postBoxRef.current && textarea.current.scrollHeight < 320) {
              postBoxRef.current.style.height = textarea.current.scrollHeight + 40 + 'px';
            }
          }
    };    

    const handlePostSubmit = async () => {
        const formData = new FormData();
        formData.append('content', content);
        formData.append('images', image);

        try {
            const BASE_URL = process.env.REACT_APP_SERVER_BASE_URL;
            const response = await axios.post(`${BASE_URL}/posts`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
            });
            console.log('Post submitted successfully:', response.data);
        } catch (error) {
            console.error('Error submitting post:', error);
        }
    };


    const [fileSelected, setFileSelected] = useState(null);
  const uplodSingleFile = (e) => {
    let file = e.target.files[0];
    setFileSelected(file);
  };



    return(
        <div className='mainBox border-start border-end' style={{ height: `${webBrowserHeight}px` }}>
            <div id="PostBox" className='border-bottom' ref={postBoxRef}>
                <div id="postProfileBox">
                    프사영역
                    {/* <img src=""/> */}
                </div>
                <div id="writePostBox">
                    {/* <input
                        type="text"
                        className='postInput'
                        placeholder="What is happenning?!"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                    /> */}
                    <textarea
                        ref={textarea}
                        value={content}
                        className='postInput'
                        placeholder="What is happenning?!"
                        onChange={handleChange}
                        // style={{ width: '200px', resize: 'none', overflow: 'hidden', minHeight: '50px' }}
                    />
                    {/* 이미지 업로드 */}
                    <input type="file" onChange={handleImageChange} />
                    {/* 글쓰기 버튼 */}
                    <button className='postButton' onClick={handlePostSubmit}>Post</button>
                </div>
            </div>


            <div className="w-50 p-3">
      <form>
        <h2 className="mb-3">
          React Functional Component Multiple Image Upload Preview Example
        </h2>
        {fileSelected && (
          <div className="mb-3">
            {<img src={URL.createObjectURL(fileSelected)} alt="..." />}
          </div>
        )}
        <div className="mb-3">
          <input
            type="file"
            className="form-control"
            onChange={uplodSingleFile}
          />
        </div>
      </form>
    </div>


        </div>
    );
}

export default Home;
