import React, {useState, useEffect} from 'react';
import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import SideNav from './pages/SideNav';
import Home from './pages/Home';
import Explore from './pages/Explore';

function App() {
  const [webBrowserHeight, setWebBrowserHeight] = useState(0);
  useEffect(() => {
    const updateHeight = () => {
      const height = window.innerHeight;
      setWebBrowserHeight(height);
    };
    // 컴포넌트가 마운트될 때와 창의 크기가 변경될 때마다 높이 업데이트
    window.addEventListener('resize', updateHeight);
    updateHeight(); // 컴포넌트가 마운트된 후에도 한 번 호출
    // 컴포넌트가 언마운트될 때 이벤트 리스너 해제
    return () => {
      window.removeEventListener('resize', updateHeight);
    };
  }, []); // 빈 배열을 전달하여 컴포넌트가 마운트될 때 한 번만 실행되도록 함



  // 로그인 리다이렉트
  const [redirectToLogin, setRedirectToLogin] = useState(false);

  // 이 함수를 통해 하위 컴포넌트에서 발생한 에러를 처리합니다.
  const handleComponentError = (error) => {
    if (error.message === '401 Unauthorized') {
      // 401 Unauthorized 에러가 발생한 경우 로그인 페이지로 리다이렉트합니다.
      setRedirectToLogin(true);
    } else {
      // 다른 종류의 에러에 대한 처리를 추가할 수 있습니다.
      // 예를 들어, 에러를 로깅하거나 사용자에게 경고 메시지를 표시할 수 있습니다.
    }
  };

  if (redirectToLogin) {
    // 리다이렉트를 실행합니다.
    return <Navigate to="/login" />;
  }



  return (
    <Router>
      <div className="App">
        <SideNav webBrowserHeight={webBrowserHeight} />
        <div className='homeBox'>
          <Routes>
            {/* 홈 페이지 */}
            <Route path="/" element={<Home webBrowserHeight={webBrowserHeight}/>} />

            {/* 탐색 페이지 */}
            <Route path="/explore" element={<Explore />} />

            {/* 로그인 페이지 */}
            <Route path="/login" element={<Login />} />

