import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useRecoilValue, useSetRecoilState } from 'recoil';
import { TokenAtom, isLoginSelector } from '../Recoil/TokenAtom';
import { useNavigate } from 'react-router-dom';

function Login() {
    const [id, setId] = useState('');
    const [password, setPassword] = useState('');
    const setAccessToken = useSetRecoilState(TokenAtom);
    const navigate = useNavigate();
    const isLogin = useRecoilValue(isLoginSelector);

    const handleSubmit = (event) => {
        event.preventDefault();

        try{
            const BASE_URL = process.env.REACT_APP_SERVER_BASE_URL;
            axios.post(`${BASE_URL}/login`, {customId:id, password}).then((res)=>{
                if(res.status === 200){
                    setAccessToken(res.status);
                    navigate('/');
                }
            })

        } catch(error){
            console.log(error);
        }
    }

    useEffect(() => {
        if(isLogin){
            return;
        } else {
            navigate("/login");
        }
    }, []);


    return (
        <div className='Login'>
            <h2>Login</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor='ID'>ID:</label>
                    <input type='text' autoFocus id='id' value={id} onChange={(e) => {setId(e.target.value)}} />
                </div>
                <div>
                    <label htmlFor='password'>Password:</label>
                    <input type='password' id='password' value={password} onChange={(e) => {setPassword(e.target.value)}} />
                </div>
                <button type='submit'>Login</button>
            </form>
        </div>
    );
}

export default Login;
