import React, { useState } from 'react';
import axios from 'axios';
import "./Home.css";

function Home({webBrowserHeight}) {

    const [content, setContent] = useState('');
    const [image, setImage] = useState(null);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    const handleChange = (event) => {
        setContent(event.target.value);
        textarea.current.style.height = 'auto'; //height 초기화
        textarea.current.style.height = textarea.current.scrollHeight + 'px';
    };    

    const handlePostSubmit = async () => {
        const formData = new FormData();
        formData.append('content', content);
        formData.append('images', image);

        try {
            const BASE_URL = process.env.REACT_APP_SERVER_BASE_URL;
            const response = await axios.post(`${BASE_URL}/posts`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
            });
            console.log('Post submitted successfully:', response.data);
        } catch (error) {
            console.error('Error submitting post:', error);
        }
    };

    return(
        <div className='mainBox border-start border-end' style={{ height: `${webBrowserHeight}px` }}>
            <div id="PostBox" className='border-bottom'>
                <div id="postProfileBox">
                    프사영역
                    {/* <img src=""/> */}
                </div>
                <div id="writePostBox">
                    {/* <input
                        type="text"
                        className='postInput'
                        placeholder="What is happenning?!"
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                    /> */}
                    <textarea
                        value={content}
                        className='postInput'
                        placeholder="What is happenning?!"
                        onChange={handleChange}
                        // style={{ width: '200px', resize: 'none', overflow: 'hidden', minHeight: '50px' }}
                    />
                    {/* 이미지 업로드 */}
                    <input type="file" onChange={handleImageChange} />
                    {/* 글쓰기 버튼 */}
                    <button onClick={handlePostSubmit}>글쓰기</button>
                </div>
            </div>
        </div>
    );
}

export default Home;
