import React, { useState } from 'react';
import axios from 'axios';
import "./Home.css";

import { useRecoilValue } from "recoil";
import { updateWebBrowserHeight } from "../Recoil/WebBrowserHeightState";

function Home() {

    const [content, setContent] = useState('');
    const [image, setImage] = useState(null);

    const handleImageChange = (e) => {
        setImage(e.target.files[0]);
    };

    const handlePostSubmit = async () => {
        const formData = new FormData();
        formData.append('content', content);
        formData.append('images', image);

        try {
            const BASE_URL = process.env.REACT_APP_SERVER_BASE_URL;
            const response = await axios.post(`${BASE_URL}/posts`, formData, {
            headers: {
                'Content-Type': 'multipart/form-data'
            }
            });
            console.log('Post submitted successfully:', response.data);
        } catch (error) {
            console.error('Error submitting post:', error);
        }
    };

    const browserHeight = useRecoilValue(updateWebBrowserHeight);
    console.log('home browserHeight' + browserHeight);

    return(
        <div className='mainBox' style={{ height: `${browserHeight}px` }}>
            <div id="PostBox">
                <div id="postProfileBox">
                    프사영역
                    {/* <img src=""/> */}
                </div>
                <div id="writePostBox">
                    <input
                    type="text"
                    placeholder="내용을 입력하세요"
                    value={content}
                    onChange={(e) => setContent(e.target.value)}
                    />
                    {/* 이미지 업로드 */}
                    <input type="file" onChange={handleImageChange} />
                    {/* 글쓰기 버튼 */}
                    <button onClick={handlePostSubmit}>글쓰기</button>
                </div>
            </div>
        </div>
    );
}

export default Home;
